import React, { useEffect, useState } from 'react';

const RoomPanel = ({ match }) => {
  const roomId = match.params.roomId;
  const [roomInfo, setRoomInfo] = useState(null);

  useEffect(() => {
    // Fetch room info using roomId from the server
    fetchRoomInfo(roomId);
  }, [roomId]);

  const fetchRoomInfo = async (roomId) => {
    try {
      const response = await fetch(`http://localhost:3030/rooms/${roomId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch room info');
      }
      const roomData = await response.json();
      setRoomInfo(roomData);
    } catch (error) {
      console.error('Error fetching room info:', error);
    }
  };

  return (
    <div>
      <h2>Room Panel</h2>
      {roomInfo ? (
        <div>
          <h3>Room Name: {roomInfo.name}</h3>
          {/* Additional room information can be displayed here */}
        </div>
      ) : (
        <p>Loading room information...</p>
      )}
    </div>
  );
};

export default RoomPanel;
