import React, { Component } from 'react';

class CreateRoom extends Component {
    state = {
        roomName: ''
    };

    handleChange = (e) => {
        this.setState({ roomName: e.target.value });
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        const { roomName } = this.state;
        const { username, connection } = this.props;

        if (!username) {
            console.error('Username is required to create a room.');
            return;
        }

        if (!roomName || !connection) {
            console.error('Room name or connection is not available.');
            return;
        }

        const message = JSON.stringify({ type: 'join', room: roomName, username });
        connection.send(message);

        // Clear the room name after submitting
        this.setState({ roomName: '' });
    }

    render() {
        return (
            <div className="create-room">
                <h2>Create New Room</h2>
                <form onSubmit={this.handleSubmit}>
                    <input
                        type="text"
                        placeholder="Enter room name"
                        value={this.state.roomName}
                        onChange={this.handleChange}
                    />
                    <button type="submit">Create</button>
                </form>
            </div>
        );
    }
}

export default CreateRoom;
