import React, { Component } from 'react'
class MessagingBox extends Component {
    messageHandler = (e) => {
	if (e.keyCode === 13) {
	    e.preventDefault()
	    this.props.getMeassage(e.target.value)
	    e.target.value = ""
	}
    }
    render() {
	return (
		<div id="messagingBox">
		<textarea onKeyDown={this.messageHandler}></textarea>
		</div>
	)
    }
}

export default MessagingBox;
