import React, { useState, useEffect } from 'react';
import RoomMessages from './roomMessages';
import { sendMessage, getRoomMessages } from './messageService';

const RoomPanel = ({ username, roomName, roomId }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    fetchRoomMessages();
  }, [roomId]);

  const fetchRoomMessages = async () => {
    try {
      const roomMessages = await getRoomMessages(roomId);
      setMessages(roomMessages);
    } catch (error) {
      console.error('Error fetching room messages:', error);
    }
  };

  const handleSendMessage = async (message) => {
    try {
      await sendMessage(roomId, message, username);
      fetchRoomMessages();
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="room-panel">
      <h2>Room Details</h2>
      <p>Room Name: {roomName}</p>
      <RoomMessages messages={messages} onSendMessage={handleSendMessage} />
    </div>
  );
};

export default RoomPanel;
