// messageService.js

import axios from 'axios';

const API_URL = 'http://localhost:3030'; // Your API URL

// Function to send a message to the server
export const sendMessage = async (roomId, message, sender) => {
  try {
    const response = await axios.post(`${API_URL}/messages`, {
      room_id: roomId,
      message: message,
      sender: sender
    });
    return response.data; // Assuming the server responds with the saved message
  } catch (error) {
    console.error('Error sending message:', error);
    throw error; // Propagate the error
  }
};

// Function to get messages for a room from the server
export const getRoomMessages = async (roomId) => {
  try {
    const response = await axios.get(`${API_URL}/messages?room_id=${roomId}`);
    return response.data; // Assuming the server responds with the messages for the room
  } catch (error) {
    console.error('Error getting messages for room:', error);
    throw error; // Propagate the error
  }
};
