import React, { Component, Fragment } from 'react';
import RoomList from './roomlist';
import CreateRoom from './createroom';
import DisplayConversation from './DisplayConversation';
import MessagingBox from './MessagingBox';

class MessagingPanel extends Component {
  state = {
    messages: [],
    rooms: [
      { id: 1, name: 'Room 1' },
      { id: 2, name: 'Room 2' },
      { id: 3, name: 'Room 3' }
    ]
  };

  connection = new WebSocket('ws://localhost:8080/');

  componentDidMount() {
    this.connection.onopen = () => {
      console.log('WebSocket connection established.');
    };

    this.connection.onmessage = (message) => {
      try {
        const data = JSON.parse(message.data);
        this.setState({ messages: [...this.state.messages, data] });
      } catch (error) {
        console.error('Error parsing message:', error);
      }
    };

    this.connection.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  }

  componentWillUnmount() {
    this.connection.close();
  }

  getMessage = (message) => {
    const data = { username: this.props.username, message: message };
    this.connection.send(JSON.stringify(data));
  };

  createRoom = (roomName) => {
    const newRoom = { id: this.state.rooms.length + 1, name: roomName };
    this.setState({ rooms: [...this.state.rooms, newRoom] });
  };

  joinRoom = (roomId) => {
    const data = { type: 'join', roomId: roomId };
    this.connection.send(JSON.stringify(data));
  };

  render() {
    return (
      <Fragment>
        <RoomList rooms={this.state.rooms} joinRoom={this.joinRoom} connection={this.connection} />
        <CreateRoom createRoom={this.createRoom} />
        <DisplayConversation messages={this.state.messages} />
        <MessagingBox getMessage={this.getMessage} />
      </Fragment>
    );
  }
}

export default MessagingPanel;
