import React from 'react';
import MessagingBox from './MessagingBox'; // Import the MessagingBox component

const RoomPanel = ({ room, messages, sendMessage }) => {
    if (!room) {
        return <div>No room selected</div>;
    }

    return (
        <div className="room-panel">
            <h2>Room Details</h2>
            <p>Room Name: {room.name}</p>
            <h3>Messages</h3>
            <div className="message-box">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <p>{message.sender}: {message.text}</p>
                    </div>
                ))}
            </div>
            {/* Render the MessagingBox component */}
            <MessagingBox room={room} sendMessage={sendMessage} />
        </div>
    );
}

export default RoomPanel;
