// RoomPanel.js

import React from 'react';
import RoomMessages from './roomMessages';
import sendMessage from './messageUtils'; // Import sendMessage function

const RoomPanel = ({ username, roomName, connection }) => {
    return (
        <div className="room-panel">
            <h2>Room Details</h2>
            <p>Room Name: {roomName}</p>
            <RoomMessages connection={connection} /> {/* Pass connection to RoomMessages */}
        </div>
    );
};

export default RoomPanel;
