import React, { Component } from 'react';
import Login from './Login';
import Signup from './Signup';
import MessagingPanel from './MessagingPanel';

class App extends Component {
  state = {
    username: null,
    authenticated: false
  };

  setUsername = (username) => {
    this.setState({ username, authenticated: true });
  };

  render() {
    const { authenticated, username } = this.state;

    // Redirect to MessagingPanel if authenticated
    if (authenticated) {
      return <MessagingPanel username={username} />;
    }

    // Render Login component if not authenticated
    return (
      <Login
        setUsername={this.setUsername}
        onLogin={() => this.setState({ authenticated: true })}
      />
    );
  }
}

export default App;
