import React, { Component } from 'react';
import Login from './Login';
import Signup from './Signup';
import MessagingPanel from './MessagingPanel';

class App extends Component {
  state = {
    username: null,
    authenticated: false,
    showSignup: false // Add a state to track whether to show the Signup component
  };

  setUsername = (username) => {
    this.setState({ username, authenticated: true });
  };

  toggleSignup = () => {
    this.setState((prevState) => ({
      showSignup: !prevState.showSignup
    }));
  };

  render() {
    const { authenticated, username, showSignup } = this.state;

    return (
      <div className="App">
        {authenticated ? (
          <MessagingPanel username={username} />
        ) : (
          <>
            <Login setUsername={this.setUsername} />
            {showSignup && <Signup />} {/* Render Signup component conditionally */}
            <button onClick={this.toggleSignup}>
              {showSignup ? 'Hide Signup' : 'Show Signup'} {/* Toggle button for showing/hiding Signup */}
            </button>
          </>
        )}
      </div>
    );
  }
}

export default App;
