import React, { Component, Fragment } from 'react';
import RoomList from './roomlist';
import CreateRoom from './createroom';
import DisplayConversation from './DisplayConversation';
import MessagingBox from './MessagingBox';

class MessagingPanel extends Component {
  state = {
    messages: [],
    rooms: [
      { id: 1, name: 'Room 1' },
      { id: 2, name: 'Room 2' },
      { id: 3, name: 'Room 3' }
    ],
    connection: null // Initialize connection state
  };

  componentDidMount() {
    const connection = new WebSocket('ws://localhost:8080/');
    connection.onopen = () => {
      console.log('WebSocket connection established.');
      this.setState({ connection }); // Update connection state after connection is established
    };

    connection.onmessage = (message) => {
      try {
        const data = JSON.parse(message.data);
        this.setState({ messages: [...this.state.messages, data] });
      } catch (error) {
        console.error('Error parsing message:', error);
      }
    };

    connection.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    this.setState({ connection }); // Update connection state
  }

  componentWillUnmount() {
    if (this.state.connection) {
      this.state.connection.close(); // Close WebSocket connection on component unmount
    }
  }

  getMessage = (message) => {
    const { connection } = this.state;
    if (connection && connection.readyState === WebSocket.OPEN) {
      const data = { username: this.props.username, message: message };
      connection.send(JSON.stringify(data)); // Send message if connection is open
    } else {
      console.error('WebSocket connection is not open.');
    }
  };

  createRoom = (roomName) => {
    const newRoom = { id: this.state.rooms.length + 1, name: roomName };
    this.setState({ rooms: [...this.state.rooms, newRoom] });
  };

  joinRoom = (roomId) => {
    const { connection } = this.state;
    if (connection && connection.readyState === WebSocket.OPEN) {
      const data = { type: 'join', roomId: roomId };
      connection.send(JSON.stringify(data)); // Send join room message if connection is open
    } else {
      console.error('WebSocket connection is not open.');
    }
  };

  render() {
    const { connection, rooms, messages } = this.state;

    return (
      <Fragment>
        {connection && <RoomList rooms={rooms} joinRoom={this.joinRoom} connection={connection} />}
        <CreateRoom createRoom={this.createRoom} />
        <DisplayConversation messages={messages} />
        <MessagingBox getMessage={this.getMessage} />
      </Fragment>
    );
  }
}

export default MessagingPanel;
